import React, { useState } from 'react';
import { useAccountContext } from '../Context/AccountInfo';
import { usePageContext } from '../Context/RenderPage1';
import { useCookies } from 'react-cookie';

function DisplLogin() {
  const [cookies, setCookie, removeCookie] = useCookies(['UserData']);
  const [useName, updateUserName] = useState('');
  const [passWord, updatePassword] = useState('');
  const [errors, setErrors] = useState({ useName: '', passWord: '' }); // State for errors

  const { accountInfo, updateAccountInfo } = useAccountContext();
  const { setCurrPage } = usePageContext();

  const handleSetCookie = (GUID) => {
    setCookie('UserData', GUID);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Validation
    let formValid = true;
    const newErrors = { useName: '', passWord: '' };

    if (!useName) {
      newErrors.useName = 'Username is required';
      formValid = false;
    }

    if (!passWord) {
      newErrors.passWord = 'Password is required';
      formValid = false;
    }

    if (!formValid) {
      setErrors(newErrors);
      return;
    }

    // Proceed with form submission
    // Handle server logic

  /*  const temp = await HandleServer(obj,"Get User")
    if(temp.status === 201|| temp.status === 200){
      updateAccountInfo({UserName: temp.data.UserName, Email: temp.data.Email, Language: temp.data.Language, Signedin: true, Budget: temp.data.Budget}) 
      handleSetCookie(temp.GUID)
      setCurrPage(1)

    }
    else{
      alert("invalid account info")
     // should work
    }
    setCurrPage(3) */
  };

  const handleSecondButton = () => {
    updateAccountInfo({ UserName: 'Guest', Email: 'unknown', Language: 'English', Signedin: false });
    removeCookie('UserData');
  };

  if (cookies.UserData === undefined) {
    return (
      <div>
        <form onSubmit={handleSubmit} className="LogIn" style={{ paddingLeft: '0' }}>
          <label>Email </label>
          <input type="Text" onChange={(e) => updateUserName(e.target.value)} style={{ border: "1px solid rgba(255, 165, 0)", borderRadius: "20px" }} />
          {errors.useName && <div style={{ color: 'red' }}>{errors.useName}</div>}

          <label>Password: </label>
          <input type="password" onChange={(e) => updatePassword(e.target.value)} style={{ border: "1px solid rgba(255, 165, 0)", borderRadius: "20px" }} />
          {errors.passWord && <div style={{ color: 'red' }}>{errors.passWord}</div>}
          <br />
          <p className="tinyText">Forget Password?</p>
          <br />
          <p></p>
          <button type="submit" style={{ borderRadius: "20px", textAlign: "center", marginLeft: "3px" }}>Log In</button>
        </form>
      </div>
    );
  } else {
    return (
      <div>
        <button onClick={handleSecondButton}>Sign out</button>
      </div>
    );
  }
}

export default DisplLogin;
